import { useEffect } from 'react';
import './App.css';
import { useAuth } from './hooks/useAuth';








function App() {
  const auth = useAuth()
  useEffect(() => {
    console.log('auth from App ->> ', auth);
  }, [auth]);
  function signin(email, password) {
    setLoading(true);
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        console.log('signIn userCredential >>>', userCredential);
        setUser(userCredential.user);
        setError(false);
      })
      .catch((error) => {
        console.warn('signIn error >>>', error);
        setError({ code: error.code, message: error.message });
        setUser(false);
      })
      .finally(() => setLoading(false));
  }
  return (
    <div className='app'>
      <div className='container'>
        <section className='forms'>
          <form className='reg_form'>
            <input name='email' type='text' placeholder='Enter your email'/>
            <input name='password' type='text' placeholder='Enter your password'/>
            <button onClick={signin} type='submit'>Registration</button>
          </form>
          <form className='log_in_form'>
            <input name='email' type='text' placeholder='Enter your email'/>
            <input name='password' type='text' placeholder='Enter your password'/>
            <button type='submit'>Log in</button>
          </form>
        </section>
      </div>
    </div>
  );
}

export default App;
